func WhatFloor(str string) int {
    result := 0
    for _, char := range str {
        if char == '(' {
            result += 1
        }else{
            result -= 1
        }
    }
    return result
}

func WhenDoesHeGoDown(str string) int {
    result := 0
    for i, char := range str {
        if char == '(' {
            result += 1
        }else{
            result -= 1
        }
        if result == -1 {
            return i+1
        }
    }
    return result
}
